

function getErrorMessage(code) {
    var msg = "";
    switch (code) {
        case 500:
            msg = 'Internal Server Error';
            break;
        case 502:
            msg = 'Bad Gateway';
            break;
        case 503:
            msg = 'Service Unavailable';
            break;
        case 504:
            msg = 'Gateway Timeout';
            break;
        case 505:
            msg = 'HTTP Version Not Supported';
            break;
        case 511:
            msg = 'Network Authentication Required';
            break;
        case 400:
            msg = 'Bad Request';
            break;
        case 401:
            msg = 'Unauthorized';
            break;
        case 403:
            msg = 'Forbidden';
            break;
        case 404:
            msg = 'Not Found';
            break;
        case 405:
            msg = 'Method Not Allowed';
            break;
        default :
            msg = "Something went wrong";
    }

    return msg + '.Please contact with System Admin.'

}
