
/* show 401, 500 etc unexpected error */

function getErrorMessage(code) {
    var msg = "";
    switch (code) {
        case 500:
            msg = 'Internal Server Error';
            break;
        case 502:
            msg = 'Bad Gateway';
            break;
        case 503:
            msg = 'Service Unavailable';
            break;
        case 504:
            msg = 'Gateway Timeout';
            break;
        case 505:
            msg = 'HTTP Version Not Supported';
            break;
        case 511:
            msg = 'Network Authentication Required';
            break;
        case 400:
            msg = 'Bad Request';
            break;
        case 401:
            msg = 'Unauthorized';
            break;
        case 403:
            msg = 'Forbidden';
            break;
        case 404:
            msg = 'Not Found';
            break;
        case 405:
            msg = 'Method Not Allowed';
            break;
        default :
            msg = "Something went wrong";
    }

    return msg + '.Please contact with System Admin.'

}


/* Set new created company value to DataTable*/

function setNewDataTableValue(company,table) {
    table.row.add({
        "id": company.id,
        "name": company.name,
        "version": company.version,
        "address": company.address,
        "createdBy": company.createdBy,
        "createdOn": company.createdOn,
        "updatedBy": company.updatedBy,
        "updatedOn": company.updatedOn
    }).draw();

};


/* Show server side message using dialog box*/

function showServerSideMessage(part1, part2, icn, msg) {
    var messageData = part1 + part2;
    $.dialogbox({
        type: 'msg',
        title: msg,
        icon: icn,
        content: messageData,
        btn: ['Ok'],
        call: [
            function () {
                $.dialogbox.close();
            }
        ]
    });
}




/* Delete row from DataTable */

function deleteDataRow(x,name) {
    var oData = $('#'+name).dataTable();
    $('#'+name).find("td:contains(" + x + ")").closest('tr').each(function () {
        oData.fnDeleteRow(this);
    });
}